      <input id="filez" type="file" onChange={handleFile} />
           {/* {file && <ImageThumb image={file} />} */}          
    </div>
    <button type ="button" onClick={  handleUpload } >Upload</button>
    </form>

    <img src= {pic} alt= "upPic" /> {/* test */}

    </div>
  );
}
