    <div>

<Popover
      popover={() => {


        return (
          <form>
    <div id="upload-box">
        <label htmlFor="files" className="btn">Select file</label>
        <input id="files" type="file" onChange={handleFile} />
           {/* {file && <ImageThumb image={file} />} */}          
    </div>
    <button type ="button" onClick={  handleUpload } >Upload</button>
    </form>



        );      

    }}
    >
      <button className="btn-submit-float">+</button>
    </Popover>









    </div>
  );
}
